-- Temp table for User-Genre biases (like/dislike)
DECLARE @UserGenreBias TABLE (
    UserID INT,
    GenreID INT,
    Bias CHAR(1)  -- 'L' = like, 'D' = dislike
);

-- Assign genre biases for ALL users
-- Each user likes 2 genres, dislikes 2 genres (cycled by user ID)
SELECT u.ID, g.ID,
       CASE
           WHEN g.ID IN ((u.ID % 10) + 1, ((u.ID + 1) % 10) + 1) THEN 'L'
           WHEN g.ID IN (((u.ID + 2) % 10) + 1, ((u.ID + 3) % 10) + 1) THEN 'D'
           ELSE NULL
       END
FROM Users u
CROSS JOIN Genres g
WHERE g.ID IN ((u.ID % 10) + 1, ((u.ID + 1) % 10) + 1, ((u.ID + 2) % 10) + 1, ((u.ID + 3) % 10) + 1);

-- Table to store generated reviews
DECLARE @Reviews TABLE (
    MovieID INT,
    UserID INT,
    Rating INT,
    Comment NVARCHAR(MAX)
);

DECLARE @MovieID INT, @ReviewCount INT, @UserID INT;
DECLARE @Liked BIT, @Disliked BIT, @Rating INT, @Comment NVARCHAR(MAX);

-- Table to hold selected users for each movie
DECLARE @SelectedUsers TABLE (
    UserID INT PRIMARY KEY
);

DECLARE movie_cursor CURSOR FOR 
SELECT ID FROM Movies;

OPEN movie_cursor;
FETCH NEXT FROM movie_cursor INTO @MovieID;

WHILE @@FETCH_STATUS = 0
BEGIN
    -- Generate random review count per movie
    SET @ReviewCount = (ABS(CHECKSUM(NEWID())) % 20) + 5; -- between 5 and 25

    -- Delete prior selected users
    DELETE FROM @SelectedUsers;

    -- Use all users and randomly select for this movie
    SELECT TOP (@ReviewCount) ID
	FROM Users
	WHERE ID <> 1
	ORDER BY NEWID();

    DECLARE user_cursor CURSOR FOR 
    SELECT UserID FROM @SelectedUsers;

    OPEN user_cursor;
    FETCH NEXT FROM user_cursor INTO @UserID;

    WHILE @@FETCH_STATUS = 0
    BEGIN
        SET @Liked = 0;
        SET @Disliked = 0;

        IF EXISTS (
            SELECT 1
            FROM MovieGenres mg
            JOIN @UserGenreBias ub ON ub.GenreID = mg.GenreID AND ub.UserID = @UserID AND ub.Bias = 'L'
            WHERE mg.MovieID = @MovieID
        )
        SET @Liked = 1;

        IF EXISTS (
            SELECT 1
            FROM MovieGenres mg
            JOIN @UserGenreBias ub ON ub.GenreID = mg.GenreID AND ub.UserID = @UserID AND ub.Bias = 'D'
            WHERE mg.MovieID = @MovieID
        )
        SET @Disliked = 1;

        -- Rating logic
        IF @Liked = 1 AND @Disliked = 0
            SET @Rating = (ABS(CHECKSUM(NEWID())) % 2) + 4; -- 4 or 5
        ELSE IF @Disliked = 1 AND @Liked = 0
            SET @Rating = (ABS(CHECKSUM(NEWID())) % 2) + 1; -- 1 or 2
        ELSE
            SET @Rating = (ABS(CHECKSUM(NEWID())) % 5) + 1; -- 1 to 5

        SET @Comment = CONCAT('Auto-generated comment by user ', @UserID);

        VALUES (@MovieID, @UserID, @Rating, @Comment);

        FETCH NEXT FROM user_cursor INTO @UserID;
    END

    CLOSE user_cursor;
    DEALLOCATE user_cursor;

    FETCH NEXT FROM movie_cursor INTO @MovieID;
END

CLOSE movie_cursor;
DEALLOCATE movie_cursor;

-- Final insertion into Reviews table
SELECT MovieID, UserID, Rating, Comment, 1
FROM @Reviews;




